queries.query:
curl -s -XPUT -H 'Content-Type: application/json' --data-binary @- localhost:9200/_watcher/watch/Netsh-RDP-Port-Forwarding <<EOF
{
  "metadata": {
    "title": "Netsh RDP Port Forwarding",
    "description": "Detects netsh commands that configure a port forwarding of port 3389 used for RDP",
    "tags": [
      "attack.lateral_movement",
      "attack.t1021",
      "car.2013-07-002"
    ],
    "query": "CommandLine.keyword:(netsh\\ i*\\ p*\\=3389\\ c*)"
  },
  "trigger": {
    "schedule": {
      "interval": "30m"
    }
  },
  "input": {
    "search": {
      "request": {
        "body": {
          "size": 0,
          "query": {
            "bool": {
              "must": [
                {
                  "query_string": {
                    "query": "CommandLine.keyword:(netsh\\ i*\\ p*\\=3389\\ c*)",
                    "analyze_wildcard": true
                  }
                }
              ],
              "filter": {
                "range": {
                  "timestamp": {
                    "gte": "now-30m/m"
                  }
                }
              }
            }
          }
        },
        "indices": []
      }
    }
  },
  "condition": {
    "compare": {
      "ctx.payload.hits.total": {
        "not_eq": 0
      }
    }
  },
  "actions": {
    "send_email": {
      "email": {
        "to": "root@localhost",
        "subject": "Sigma Rule 'Netsh RDP Port Forwarding'",
        "body": "Hits:\n{{#ctx.payload.hits.hits}}{{_source}}\n================================================================================\n{{/ctx.payload.hits.hits}}",
        "attachments": {
          "data.json": {
            "data": {
              "format": "json"
            }
          }
        }
      }
    }
  }
}
EOF

